// Simple PDF.js worker fallback for local development
// This prevents the CDN fetch errors when external requests fail
self.addEventListener('message', function(e) {
  // Basic worker implementation that handles PDF.js worker requests
  if (e.data && e.data.type === 'test') {
    self.postMessage({ type: 'test', success: true });
  }
});

// Minimal PDF.js worker compatibility
if (typeof window === 'undefined') {
  self.window = self;
}

console.log('PDF.js worker loaded successfully');